manifest.install:
#银行 CREATE TABLE IF NOT EXISTS `ims_lanyu_bank` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(11) DEFAULT NULL, `bank_name` varchar(255) NOT NULL, `bank_code` varchar(255) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=14 DEFAULT CHARSET=utf8;
#款项等记表 CREATE TABLE IF NOT EXISTS `ims_lanyu_sign` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(11) DEFAULT NULL, `bank_id` int(10) NOT NULL, `amount` float(10,2) NOT NULL COMMENT '金额', `input_id` int(10) NOT NULL, `sign_id` int(10) NOT NULL COMMENT '签款业务员id', `status` int(10) NOT NULL DEFAULT '1' COMMENT '状态，1为未审核，2为已审核', `create_time` varchar(255) COMMENT '入账时间', `bank_user` varchar(255), `remark` varchar(255), `invoice` int(1) NOT NULL DEFAULT '0' COMMENT '发票状态，1为开了，0 为未开', `examine_time` varchar(255), PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=14 DEFAULT CHARSET=utf8;
#签款表 CREATE TABLE IF NOT EXISTS `ims_lanyu_sign_user` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(11) DEFAULT NULL, `sign_id` int(10) NOT NULL, `member_amount` float(10,2) NOT NULL COMMENT '金额', `member_name` varchar(255) NOT NULL, `member_code` varchar(255) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=14 DEFAULT CHARSET=utf8;
